actions.script:
localVars.clock_id = map_corners_to_clock(localVars.tile);
localVars.pos_id = map_corners_to_pos(localVars.tile);
localVars.circle_id = map_corners_to_circle(localVars.tile);
//alert(localVars.tile + " " + localVars.clock_id + " " + localVars.pos_id);

let obs = runtime.objects.Obstacle.getFirstPickedInstance();
let car = runtime.objects.car_edge_collision.getFirstPickedInstance();
let collisions = sprite_collision_points(car.uid, obs.uid, 5, runtime);
for (let [x, y] of collisions) {
	if (Math.random() * runtime.objects.EdgeSparks.getAllInstances().length < 4) {
		let new_spark = runtime.objects.EdgeSparks.createInstance("Level", x, y, false);
		new_spark.angleDegrees = car.angleDegrees - 180;
	}
}

let picked_car = runtime.objects.Car.getFirstPickedInstance();
let picked_collision = runtime.objects.car_edge_collision.getFirstPickedInstance();
[localVars.vx1, localVars.vy1, localVars.vx2, localVars.vy2] = car_collision_resolution(picked_collision.instVars.AttachedToCarUID, picked_car.uid, runtime);
